import pandas as pd
import os

# --- Configuration ---
# This should point to the CSV generated by wlasl_parser.py for your 10 words
PROCESSED_DATA_CSV = 'wlasl_10_words_processed.csv' 

# Base directory where your script is running from
# The video_path column in the CSV is relative to this base.
BASE_DIR = os.getcwd() 

print(f"--- Listing Video Files and Sign Names for Dataset: {PROCESSED_DATA_CSV} ---")

if not os.path.exists(PROCESSED_DATA_CSV):
    print(f"Error: Processed data CSV not found at {PROCESSED_DATA_CSV}.")
    print("Please ensure wlasl_parser.py has been run to create this file.")
    exit()

try:
    df_videos = pd.read_csv(PROCESSED_DATA_CSV)
    print(f"Loaded {len(df_videos)} video entries.")

    print("\nExact video paths and their corresponding sign names (glosses):")
    count = 0
    for index, row in df_videos.iterrows():
        gloss = row['gloss'] # <-- NEW: Get the gloss (sign name)
        relative_video_path = row['video_path']
        full_video_path = os.path.join(BASE_DIR, relative_video_path) # Construct full path
        
        if os.path.exists(full_video_path):
            print(f"  Sign: {gloss.upper():<10} | Path: {full_video_path}") # <-- UPDATED PRINT
            count += 1
        else:
            print(f"  [NOT FOUND ON DISK] Sign: {gloss.upper():<10} | Path: {full_video_path}") # <-- UPDATED PRINT
            
    print(f"\nTotal video files found on disk: {count} out of {len(df_videos)} listed.")

except Exception as e:
    print(f"An error occurred: {e}")